	private static final long serialVersionUID = 1L;
	List<Map<String, String>> _isoCountryData;
 
	
	public List<Map<String, String>> getAllIsoCountries(){
		List<Map<String, String>> list = new ArrayList<Map<String, String>>();
		
		for (Map<String, String> countryMap : _isoCountryData) {
			
			Map<String, String> newMap = new HashMap<String, String>();
			newMap.put(COUNTRY_NAME, countryMap.get(COUNTRY_NAME));
			newMap.put(ALPHA2_CODE, countryMap.get(ALPHA2_CODE));
			newMap.put(LATITUDE, countryMap.get(LATITUDE));
			newMap.put(LONGITUDE, countryMap.get(LONGITUDE));
			
            list.add(newMap);
		}

		return list;
	}

	public List<Map<String, String>> getIsoCountryForCode(String lookupCode) {
		List<Map<String, String>> list = new ArrayList<Map<String, String>>();

		for (Map<String, String> countryMap : _isoCountryData) {
			String alpha2Code = countryMap.get(ALPHA2_CODE);
			String alpha3Code = countryMap.get(ALPHA3_CODE);
			String numericCode = countryMap.get(NUMERIC_CODE);
			
			if (alpha2Code.equalsIgnoreCase(lookupCode)
					|| alpha3Code.equalsIgnoreCase(lookupCode)
					|| numericCode.equalsIgnoreCase(lookupCode)) {
				Map<String, String> newMap = new HashMap<String, String>(
						countryMap);
				list.add(newMap);
			}
		}

		return list;
	}
	 
	
