	private List<Map<String, String>> getGeoCountryData(Document doc) {
		NodeList nList = doc.getElementsByTagName("row");
		List<Map<String, String>> list = new ArrayList<Map<String, String>>();

		for (int temp = 0; temp < nList.getLength(); temp++) {
			Node nNode = nList.item(temp);
		 
			if (nNode.getNodeType() == Node.ELEMENT_NODE) {
				Map<String, String> map = new HashMap<String, String>();
				Element eElement = (Element) nNode;
				
				map.put(COUNTRY_NAME, eElement.getElementsByTagName(COUNTRY_NAME)
						.item(0).getTextContent());
				map.put(ALPHA2_CODE, eElement.getElementsByTagName(ALPHA2_CODE)
						.item(0).getTextContent());
				map.put(ALPHA3_CODE, eElement.getElementsByTagName(ALPHA3_CODE)
						.item(0).getTextContent());
				map.put(NUMERIC_CODE, eElement.getElementsByTagName(NUMERIC_CODE)
						.item(0).getTextContent());
				map.put(LATITUDE, eElement.getElementsByTagName(LATITUDE)
						.item(0).getTextContent());
				map.put(LONGITUDE, eElement.getElementsByTagName(LONGITUDE)
						.item(0).getTextContent());
 
				list.add(map);
			}
		}

		return list;
	}
 	
	private final String COUNTRY_NAME = "name";
	private final String ALPHA2_CODE = "alpha2_code";
	private final String ALPHA3_CODE = "alpha3_code";
	private final String NUMERIC_CODE = "numeric_code";
	private final String LATITUDE = "latitude";
	private final String LONGITUDE = "longitude";
	
